CREATE DEFINER=`root`@`localhost` PROCEDURE `obtenerMesasLibres`(IN `pfecha_hora` INT(15))
BEGIN
	SET @fecha = FROM_UNIXTIME(pfecha_hora);
    SET @pre = (SELECT duracion_reserva FROM GENERAL) - 1;
    
	SELECT *
    FROM MESAS
    WHERE id_mesa NOT IN(
		SELECT id_mesa
        FROM RESERVACIONES
		WHERE DATE(hora_reservacion) = DATE(@fecha) 
		AND HOUR(hora_reservacion) = HOUR(@fecha)
        AND HOUR(@fecha) = (HOUR(hora_reservacion) - @pre)
	);
END ;;
