CREATE DEFINER=`root`@`%` PROCEDURE `obtener_reporte_platos`(pfecha_inicio date, pfecha_final date)
BEGIN
	SELECT 
		hora,
		MAX(cantidad_platos) AS cant_platos,
		nombre_plato
	FROM
		(SELECT
			CONCAT(HOUR(fa.fecha_hora), ":00") AS hora,
			COUNT(1) AS cantidad_platos,
			pa.nombre_plato
		FROM PLATOS_X_ORDEN AS po
		INNER JOIN PLATOS AS pa ON pa.id_plato = po.id_plato
		INNER JOIN ORDENES AS ord ON ord.id_orden = po.id_orden
		INNER JOIN FACTURAS AS fa ON ord.id_orden = fa.id_orden
		WHERE fa.estado = 'Facturada'
		AND DATE(fa.fecha_hora) BETWEEN pfecha_inicio AND pfecha_final
		GROUP BY HOUR(fa.fecha_hora),
			po.id_plato
		) T1
	GROUP BY hora
    ORDER BY cant_platos DESC;
END ;;
